mapper.select:
	SELECT * FROM RES_UPDATETABLEINFOS
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RES_USETECHNIQUEINFOS
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RESUDP_CATALOGS
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RESUDP_CATALOGFULLTREE
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RESUDP_DOCUMENTINFOS
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RESUDP_SYSTEMINFOS
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RESUDP_QUESTIONS
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RESUDP_QUESTIONLAWREFS
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RESUDP_QUESTIONEXAMPLES
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT * FROM RESUDP_LAWEXAMPLES
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}

	SELECT USERID,USERNAME,EMAIL as LOGINNAME FROM SYS_USERS
    WHERE ${whereClause}
    ORDER BY ${orderbyClause}
